var WsCommonUtils = (function() {

    return {
        'responseBuilder': responseBuilder,
        "isNotificationRequest": isNotificationRequest
    };

    function responseBuilder(responseObj, status, responseBody, headers, contentType, location) {
        try {

            responseObj.setStatus((status) ? status : "200");
            responseObj.setBody((responseBody) ? responseBody : "Message received");
            if (!gs.nil(headers)) {
                responseObj.setHeaders(headers);
            }
            if (!gs.nil(location)) {
                responseObj.setLocation();
            }
            if (!gs.nil(contentType)) {
                responseObj.setContentType();
            }
			return responseObj;

        } catch (err) {
            gs.addErrorMessage("WsCommonUtils::responseBuilder >> Error occured \n " + err.toString());
            WsErrorUtils.TechnicalError(err.toString());
        }
    }
    /***	 * isNotificationRequest -  will validate the input request is of Notification or not by checking the 'eventid' parameter in the requestbody
            * @param: (M) requestBody - request object as created in inbound scripted API
            * @param: (M) responseObj - request object as created in inbound scripted API

            * @return- responseObj
            *
            ***/

    function isNotificationRequest(requestBody) {
        try {
            /* Validation to accept only String/Object to verify it is a Notifiaton request or not */
            if ((typeof requestBody) !== 'string' && (typeof requestBody) === 'object') {
                throw "The input requestbody to function 'isNotificationRequest' should be either Object/String ";
            }

            if ((typeof requestBody) === 'string') {
                requestBody = JSON.parse(requestBody); // converting into JSON object if the passed object is String
            }
            return !gs.nil(requestBody['eventId']);

        } catch (err) {
            gs.addErrorMessage("WsCommonUtils::isNotificationRequest >> Error occured \n " + err.toString());
            WsErrorUtils.TechnicalError(err.toString());
        }
    }


