        } catch (err) {
            gs.addErrorMessage("WsCommonUtils::responseBuilder >> Error occured \n " + err.toString());
            WsErrorUtils.TechnicalError(err.toString());
        }
    }


    function f2(requestObj) {
        try {

        } catch (err) {
            gs.addErrorMessage("WsCommonUtils::responseBuilder >> Error occured \n " + err.toString());
            WsErrorUtils.TechnicalError(err.toString());
        }
    }


    function f3(requestObj) {
        try {

        } catch (err) {
            gs.addErrorMessage("WsCommonUtils::responseBuilder >> Error occured \n " + err.toString());
            WsErrorUtils.TechnicalError(err.toString());
        }
    }




})();
