sys_script_include.script:
var WsCommonUtils = Class.create();
WsCommonUtils.prototype = {
    initialize: function() {
    },
	responseBuilder:function(responseObj, status, body, headers,contentType,location){
		try{

				responseObj.setStatus((status)?status:"200");
			responseObj.setBody((body)?body:"Message received");



		}catch(err){
			gs.addErrorMessage("WsCommonUtils::responseBuilder >> Error occured \n "+err.toString());
		}
	},

    type: 'WsCommonUtils'
};
