sys_script_include.script:
var Ws_ErrorConstants = (function() {


    /* Define the following syntax to define Error_code
    ERROR_OBJECT_NAME:[<HTTP_STATUS_CODE>,<Message>,<Details>]
    */
    function MandtoryFieldsMissing(msg) {
		var status = "400";
		var message = "Mandatory fields validaiton failed";
		var details = "The list of mandatory fields missing to insert a record are : "+msg;
        return _generateErrorResponse(status,message ,details );
    }

    function NotFoundError() {
        return _generateErrorResponse("400", "Mandatory fields validaiton failed", "The list of mandatory fields missing to insert a record are : ");
    }

    function InvalidInput() {
        return _generateErrorResponse("400", "Mandatory fields validaiton failed", "The list of mandatory fields missing to insert a record are : ");
    }


    function _generateErrorResponse(status, message, details) {
        var myError = new sn_ws_err.ServiceError();
        myError.setStatus(status);
        myError.setMessage(message);
        myError.setDetail(details);
        return myError;

    }

    return {
        'MandtoryFieldsMissing': MandtoryFieldsMissing,
        'NotFoundError': NotFoundError,
        'InvalidInput': InvalidInput
    };

})();
