sys_script_include.script:
var WsErrorUtils = (function() {

    return {
        'MandtoryFieldsMissing': MandtoryFieldsMissing,
        'NotFoundError': NotFoundError,
        'InvalidInput': InvalidInput,
        "ServiceNotImplemented": ServiceNotImplemented,
        "TechnicalError": TechnicalError,
		"EventForbidden":EventForbidden
    };

    function EventForbidden(msg) {
        var status = "403";
        var message = "Unable to process the event/notification received.";
        var details = "The provided event cannot be processed under this REST resource endpoint. " + msg;
        return _generateErrorResponse(status, message, details);
    }

    function TechnicalError(msg) {
        var status = "500";
        var message = "Servicenow technical Error";
        var details = "An internal error occured while processing the request. Please raise a ticket with the HelpDesk team. The error details are :" + msg;
        return _generateErrorResponse(status, message, details);
    }


    function ServiceNotImplemented(msg) {
        var status = "501";
        var message = "API resource request processing is yet to be implemented";
        var details = "The requested API is under maintainance and hence the request cannot be processed. " + msg;
        return _generateErrorResponse(status, message, details);
    }

    function MandtoryFieldsMissing(msg) {
        var status = "400";
        var message = "Mandatory fields validaiton failed";
        var details = "The list of mandatory fields missing to insert a record are : " + msg;
        return _generateErrorResponse(status, message, details);
    }

    function NotFoundError(msg) {
        var status = "404";
        var message = "Requested resource is not available";
        var details = "The resource with the given id is not available in the current database. " + msg;
        return _generateErrorResponse(status, message, details);
    }

    function InvalidInput(msg) {
        var status = "400";
        var message = "Invalid Request Data";
        var details = "There a technical/functional error due to invalid request data. The details are:  " + msg;
        return _generateErrorResponse(status, message, details);
    }

	function InvalidEvent(msg) {
        var status = "401";
        var message = "Invalid Event Notification";
        var details = "There a technical/functional error due to invalid request data. The details are:  " + msg;
        return _generateErrorResponse(status, message, details);
    }


    function _generateErrorResponse(status, message, details) {
        var myError = new sn_ws_err.ServiceError();
        myError.setStatus(status);
        myError.setMessage(message);
        myError.setDetail(details);
        return myError;

    }



})();
